        //     }
        //
        // fetchMyTrades (private)
        //
        //     {
        //         "fee" => 20.1374935,
        //         "feeRate" => 0.0005,
        //         "feeCurrency" => "USD",
        //         "future" => "EOS-0329",
        //         "id" => 11215,
        //         "liquidity" => "taker",
        //         "market" => "EOS-0329",
        //         "baseCurrency" => null,
        //         "quoteCurrency" => null,
        //         "orderId" => 8436981,
