        //                     "shortOrderSize" => 1732.09,
        //                     "side" => "sell",
        //                     "size" => 0.23,
        //                     "unrealizedPnl" => 0,
        //                 ),
        //             ),
        //         ),
        //     }
        //
        $result = $this->safe_value($response, 'result', array());
        $maker = $this->safe_number($result, 'makerFee');
        $taker = $this->safe_number($result, 'takerFee');
        $tradingFees = array();
        for ($i = 0; $i < count($this->symbols); $i++) {
            $symbol = $this->symbols[$i];
            $tradingFees[$symbol] = array(
                'info' => $response,
                'symbol' => $symbol,
                'maker' => $maker,
                'taker' => $taker,
                'percentage' => true,
                'tierBased' => true,
            );
        }
        return $tradingFees;
    }

    public function fetch_funding_rate_history($symbol = null, $since = null, $limit = null, $params = array ()) {
        /**
         * fetches historical funding rate prices
         * @param {str|null} $symbol unified $symbol of the $market to fetch the funding rate history for
         * @param {int|null} $since $timestamp in ms of the earliest funding rate to fetch
         * @param {int|null} $limit the maximum amount of ~@link https://docs.ccxt.com/en/latest/manual.html?#funding-rate-history-structure funding rate structures~ to fetch
         * @param {dict} $params extra parameters specific to the ftx api endpoint
         * @param {int|null} $params->till extra parameters specific to the ftx api endpoint
         * @return {[dict]} a list of ~@link https://docs.ccxt.com/en/latest/manual.html?#funding-rate-history-structure funding rate structures~
         */
        $this->load_markets();
        $request = array();
        if ($symbol !== null) {
            $market = $this->market($symbol);
            $symbol = $market['symbol'];
            $request['future'] = $market['id'];
        }
        if ($since !== null) {
            $request['start_time'] = intval($since / 1000);
        }
        $till = $this->safe_integer($params, 'till'); // unified in milliseconds
        $endTime = $this->safe_string($params, 'end_time'); // exchange-specific in seconds
        $params = $this->omit($params, array( 'end_time', 'till' ));
        if ($till !== null) {
            $request['end_time'] = intval($till / 1000);
        } elseif ($endTime !== null) {
            $request['end_time'] = $endTime;
        }
        $response = $this->publicGetFundingRates (array_merge($request, $params));
        //
        //     {
        //        "success" => true,
        //        "result" => array(
        //          {
        //            "future" => "BTC-PERP",
        //            "rate" => 0.0025,
        //            "time" => "2019-06-02T08:00:00+00:00"
        //          }
        //        )
        //      }
        //
        $result = $this->safe_value($response, 'result', array());
        $rates = array();
        for ($i = 0; $i < count($result); $i++) {
            $entry = $result[$i];
            $marketId = $this->safe_string($entry, 'future');
            $timestamp = $this->parse8601($this->safe_string($entry, 'time'));
            $rates[] = array(
                'info' => $entry,
                'symbol' => $this->safe_symbol($marketId),
                'fundingRate' => $this->safe_number($entry, 'rate'),
                'timestamp' => $timestamp,
                'datetime' => $this->iso8601($timestamp),
            );
        }
        $sorted = $this->sort_by($rates, 'timestamp');
        return $this->filter_by_symbol_since_limit($sorted, $symbol, $since, $limit);
    }

    public function parse_balance($response) {
        $result = array(
            'info' => $response,
        );
        $balances = $this->safe_value($response, 'result', array());
        for ($i = 0; $i < count($balances); $i++) {
            $balance = $balances[$i];
            $code = $this->safe_currency_code($this->safe_string($balance, 'coin'));
            $account = $this->account();
            $account['free'] = $this->safe_string_2($balance, 'availableWithoutBorrow', 'free');
            $account['total'] = $this->safe_string($balance, 'total');
            $result[$code] = $account;
        }
        return $this->safe_balance($result);
    }

