    public function sign($path, $api = 'private', $method = 'GET', $params = array (), $headers = null, $body = null) {
        // Attach header and other necessary parameters to the API $request->
        // This is called before evert REST API $request is thrown to the server.
        $request = '/' . $this->implode_params($path, $params);
        $query = $this->omit($params, $this->extract_params($path));
        if ($method === 'GET') {
            // Append URL variables separated by "?"
            if ($query) {
                $request .= '?' . $this->urlencode($query);
            }
        } else if (($method === 'POST') || ($method === 'PUT')) {
            $body = $this->json($query);
        }
        $this->check_required_credentials();
        if (!$this->apiKey) {
            $this->apiKey = $this->fetch_token();
        }
        $url = $this->implode_params($this->urls['api'], array( 'ipaddr' => $this->ipaddr )) . $request;
        $headers = array(
            'X-API-KEY' => $this->apiKey,
            'Content-Type' => 'application/json',
        );
        return array( 'url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers );
    }

    public function parse_symbol($symbol) {
        // Parse $symbol string to get code and $exchange ID
        // {$symbol} = {code}@{exchnage_id}/{$fiat} e.g., 8306@1/JPY
        // NOTE => code in CCXT/freqtrade corresponds to "Symbol" in Kabus.
        $market = explode('/', $symbol)[0];
        $fiat = explode('/', $symbol)[1];
        $stock_code = explode('@', $market)[0];
        $exchange = intval(explode('@', $market)[1]);
        return array( 'Code' => $stock_code, 'Exchange' => $exchange, 'Market' => $market, 'Fiat' => $fiat );
    }

    public function prepare_order($symbol, $type, $side, $amount, $price) {
        // 現物株式取引用のパラメタ設定
        $symbolParam = $this->parse_symbol($symbol);
        if ($type === 'market') {
            $price = 0; // 成行執行→$price=0
        }
        return array(
            'Symbol' => $symbolParam['Code'],
            'Exchange' => $symbolParam['Exchange'],
            'Side' => $this->safe_string(array( 'sell' => '1', 'buy' => '2' ), $side),
            'DelivType' => $this->safe_integer(array( 'sell' => 0, 'buy' => 2 ), $side), // 現物買→預り金
