    public function parse_trade($trade, $market = null) {
        // {
        //      'ExecutionID' => None,
        //      'AccountType' => '4',
        //      'Symbol' => '5020',
        //      'SymbolName' => 'ＥＮＥＯＳホールディングス',
        //      'Exchange' => '1',
        //      'ExchangeName' => '東証プ',
        //      'Price' => '454.0',
        //      'LeavesQty' => '100.0',
        //      'HoldQty' => '0.0',
        //      'Side' => '2',
        //      'CurrentPrice' => '456.2',
        //      'Valuation' => '45620.0',
        //      'ProfitLoss' => '220.0',
        //      'ProfitLossRate' => '0.484581497797356828193832600'
        // }
        $price = $this->safe_float($trade, 'Price');
        $side = $this->safe_string_lower(array( '1' => 'sell', '2' => 'buy' ), $trade['Side']);
        $leavesQty = $this->safe_float($trade, 'LeavesQty');
        $holdQty = $this->safe_float($trade, 'HoldQty');
        $amount = $leavesQty - $holdQty;
        return $this->safe_trade(array(
            'timestamp' => null,
            'datetime' => null,
            'symbol' => $market['symbol'],
            'id' => $market['symbol'],
            'order' => null,
            'type' => null,
            'side' => $side,
            'takerOrMaker' => null,
            'price' => $price,
            'amount' => $amount,
            'cost' => null,
            'fee' => null,
            'info' => $trade,
        ), $market);
    }

    public function fetch_trades($symbol, $since = null, $limit = null, $params = array ()) {
        $this->load_markets();
        $market = $this->market($symbol);
        $stock_code = $this->parse_symbol($symbol)['Code'];
        $request = array( 'product' => 0, 'addInfo' => 'true', 'symbol' => $stock_code );
        $response = $this->privateGetPositions (array_merge($request, $params));
        return $this->parse_trades($response, $market, $since, $limit);
    }

    public function parse_order($order, $market = null) {
        //     array('AccountType' => 2,
        //       'CumQty' => 0.0,
        //       'DelivType' => 2,
        //       'Details' => [array('Commission' => 0.0,
        //                    'CommissionTax' => 0.0,
        //                    'DelivDay' => 20220427,
        //                    'ExchangeID' => None,
        //                    'ExecutionDay' => None,
        //                    'ExecutionID' => None,
        //                    'ID' => '20220423A01N86096041',
        //                    'OrdType' => 1,
        //                    'Price' => 0.0,
        //                    'Qty' => 100.0,
        //                    'RecType' => 1,
        //                    'SeqNum' => 1,
        //                    'State' => 3,
        //                    'TransactTime' => '2022-04-23T14:31:16.652838+09:00'),
        //                   array('Commission' => 0.0,
        //                    'CommissionTax' => 0.0,
        //                    'DelivDay' => 20220427,
        //                    'ExchangeID' => None,
        //                    'ExecutionDay' => None,
        //                    'ExecutionID' => None,
        //                    'ID' => '20220423B01N86096042',
        //                    'OrdType' => 1,
        //                    'Price' => 0.0,
        //                    'Qty' => 100.0,
        //                    'RecType' => 4,
        //                    'SeqNum' => 2,
        //                    'State' => 1,
        //                    'TransactTime' => '2022-04-23T14:31:16.652838+09:00')],
        //       'Exchange' => 1,
        //       'ExchangeName' => '東証ス',
        //       'ExpireDay' => 20220425,
        //       'ID' => '20220423A01N86096041',
        //       'OrdType' => 1,
        //       'OrderQty' => 100.0,
        //       'OrderState' => 1,
        //       'Price' => 0.0,
        //       'RecvTime' => '2022-04-23T14:31:16.652838+09:00',
        //       'Side' => '2',
        //       'State' => 1,
        //       'Symbol' => '9318',
        //       'SymbolName' => 'アジア開発キャピタル'),
        //     }
        $id = $this->safe_string($order, 'ID');
        $price = $this->safe_float($order, 'Price');
        $amount = $this->safe_float($order, 'OrderQty');
        $cumQty = $this->safe_float($order, 'CumQty');
        $side = $this->safe_string_lower(array( '1' => 'sell', '2' => 'buy' ), $order['Side']);
        $timestamp = $this->parse8601($this->safe_string($order, 'RecvTime'));
        $stock_code = $this->safe_string($order, 'Symbol');
        $exchange = $this->safe_string($order, 'Exchange');
        $order_type = 'limit';
        if ($price === 0) {
            $order_type = 'market';
        }
        // Order $status is one of ['open', 'closed', 'canceled', 'expired', 'rejected']
        $n_details = is_array($order['Details']) ? count($order['Details']) : 0;
        if ($n_details < 1) {
            throw new ExchangeError($this->id . ' expects to have at least 1 detail per $order but 0 given');
        }
        $lastDetail = $order['Details'][$n_details - 1];
        // Get the latest state from the Details
        $status = $this->safe_string(array( '1' => 'open', '3' => 'closed' ), $lastDetail['State']);
        return $this->safe_order(array(
            'id' => $id,
            'clientOrderId' => null,
            'info' => $order,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'lastTradeTimestamp' => null,
            'status' => $status,
            'symbol' => $stock_code . '@' . $exchange . '/JPY',
            'type' => $order_type,
            'timeInForce' => null,
            'postOnly' => null,
            'side' => $side,
            'price' => $price,
            'stopPrice' => null,
            'cost' => null,
            'amount' => $amount,
            'filled' => $cumQty,
            'remaining' => $amount - $cumQty,
            'fee' => null,
            'average' => null,
            'trades' => null,
        ), $market);
    }

    public function fetch_orders($since = null, $limit = 100, $params = array ()) {
        $this->load_markets();
        $params['product'] = 0;
        $response = $this->privateGetOrders ($params);
        return $this->parse_orders($response);
    }

    public function fetch_open_orders($since = null, $limit = 100, $params = array ()) {
        $orders = $this->fetch_orders();
        $open_orders = array();
        for ($i = 0; $i < count($orders); $i++) {
            if ($orders[$i]['status'] === 'open') {
                $open_orders[] = $orders[$i];
            }
        }
        return $open_orders;
    }

    public function fetch_closed_orders($since = null, $limit = 100, $params = array ()) {
        $orders = $this->fetch_orders();
        $closed_orders = array();
        for ($i = 0; $i < count($orders); $i++) {
            if ($orders[$i]['status'] === 'closed') {
                $closed_orders[] = $orders[$i];
            }
        }
        return $closed_orders;
    }

    public function fetch_order($id, $symbol = null, $params = array ()) {
        if ($symbol === null) {
            throw new ArgumentsRequired($this->id . ' fetchOrder() requires a `$symbol` argument');
        }
        $orders = $this->fetch_orders();
        $ordersById = $this->index_by($orders, 'id');
        if (is_array($ordersById) && array_key_exists($id, $ordersById)) {
            return $ordersById[$id];
        }
        throw new OrderNotFound($this->id . 'No order found with $id ' . $id);
    }

    public function cancel_order($id, $params = array ()) {
        $this->load_markets();
        $body = array(
            'OrderID' => $id,
            'Password' => $this->kabucom_password,
        );
        return $this->privatePutCancelorder (array_merge($body, $params));
    }

    public function parse_balance($response) {
        // Parse and organize balance information.
        $result = array( 'info' => $response );
        $account = $this->account();
        $account['free'] = $this->safe_float($response, 'StockAccountWallet');
        $result['JPY'] = $account;
        return $result;
    }

    public function fetch_balance($params = array ()) {
        // Fetch account balance information
        $this->load_markets();
        $response = $this->privateGetWalletCash ($params);
        // {
